import React, { useState, useEffect } from 'react';
import { FaPlus, FaSearch, FaFilter, FaSort, FaEllipsisV, FaEdit, FaTrash, FaEye, FaBox, FaWarehouse, FaChartBar, FaChevronDown } from 'react-icons/fa';

const Inventory = () => {
  const [inventory, setInventory] = useState([]);
  const [filteredInventory, setFilteredInventory] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState('all');
  const [sortConfig, setSortConfig] = useState({ key: 'name', direction: 'asc' });
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);

  // Sample inventory data
  useEffect(() => {
    const sampleInventory = [
      {
        id: 1,
        name: 'Canon EOS R5',
        category: 'Camera',
        brand: 'Canon',
        model: 'EOS R5',
        quantity: 5,
        available: 3,
        totalValue: 15000,
        location: 'Warehouse A',
        status: 'Available',
        lastUpdated: '2025-09-01'
      },
      {
        id: 2,
        name: 'Sony A7 III',
        category: 'Camera',
        brand: 'Sony',
        model: 'A7 III',
        quantity: 3,
        available: 1,
        totalValue: 9000,
        location: 'Warehouse B',
        status: 'Low Stock',
        lastUpdated: '2025-08-30'
      },
      {
        id: 3,
        name: 'Nikon Z7 II',
        category: 'Camera',
        brand: 'Nikon',
        model: 'Z7 II',
        quantity: 2,
        available: 0,
        totalValue: 6000,
        location: 'Warehouse A',
        status: 'Out of Stock',
        lastUpdated: '2025-08-29'
      },
      {
        id: 4,
        name: '24-70mm f/2.8 Lens',
        category: 'Lens',
        brand: 'Canon',
        model: 'EF 24-70mm f/2.8L',
        quantity: 4,
        available: 2,
        totalValue: 8000,
        location: 'Warehouse B',
        status: 'Available',
        lastUpdated: '2025-09-01'
      },
      {
        id: 5,
        name: '70-200mm f/2.8 Lens',
        category: 'Lens',
        brand: 'Canon',
        model: 'EF 70-200mm f/2.8L',
        quantity: 3,
        available: 1,
        totalValue: 6000,
        location: 'Warehouse A',
        status: 'Low Stock',
        lastUpdated: '2025-08-31'
      },
      {
        id: 6,
        name: 'Tripod Pro',
        category: 'Accessory',
        brand: 'Manfrotto',
        model: 'MT055XPRO3',
        quantity: 8,
        available: 6,
        totalValue: 1200,
        location: 'Warehouse C',
        status: 'Available',
        lastUpdated: '2025-09-01'
      },
      {
        id: 7,
        name: 'Flash Speedlite',
        category: 'Accessory',
        brand: 'Canon',
        model: 'Speedlite 600EX II',
        quantity: 6,
        available: 4,
        totalValue: 1800,
        location: 'Warehouse B',
        status: 'Available',
        lastUpdated: '2025-08-30'
      },
      {
        id: 8,
        name: 'Memory Card 128GB',
        category: 'Accessory',
        brand: 'SanDisk',
        model: 'Extreme Pro',
        quantity: 20,
        available: 15,
        totalValue: 1000,
        location: 'Warehouse A',
        status: 'Available',
        lastUpdated: '2025-09-01'
      }
    ];
    setInventory(sampleInventory);
    setFilteredInventory(sampleInventory);
  }, []);

  // Filter and search functionality
  useEffect(() => {
    let filtered = inventory;

    if (searchTerm) {
      filtered = filtered.filter(item =>
        item.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        item.brand.toLowerCase().includes(searchTerm.toLowerCase()) ||
        item.model.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (filterCategory !== 'all') {
      filtered = filtered.filter(item => item.category === filterCategory);
    }

    if (sortConfig.key) {
      filtered.sort((a, b) => {
        const aValue = a[sortConfig.key];
        const bValue = b[sortConfig.key];
        
        if (aValue < bValue) {
          return sortConfig.direction === 'asc' ? -1 : 1;
        }
        if (aValue > bValue) {
          return sortConfig.direction === 'asc' ? 1 : -1;
        }
        return 0;
      });
    }

    setFilteredInventory(filtered);
    setCurrentPage(1);
  }, [searchTerm, filterCategory, sortConfig, inventory]);

  const handleSort = (key) => {
    let direction = 'asc';
    if (sortConfig.key === key && sortConfig.direction === 'asc') {
      direction = 'desc';
    }
    setSortConfig({ key, direction });
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Available':
        return 'success';
      case 'Low Stock':
        return 'warning';
      case 'Out of Stock':
        return 'danger';
      default:
        return 'secondary';
    }
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredInventory.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(filteredInventory.length / itemsPerPage);

  return (
    <div className="inventory-page">
      {/* Header */}
      <div className="page-header">
        <div className="header-content">
          <div className="header-left">
            <h1 className="page-title">Inventory Management</h1>
            <p className="page-description">Track and manage your rental inventory levels</p>
          </div>
          <div className="header-actions">
            <button className="btn btn-primary">
              <FaPlus /> New Product
            </button>
            <button className="btn btn-secondary">
              <FaChartBar /> Export Report
            </button>
          </div>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="stats-container">
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-icon">
              <FaBox />
            </div>
            <div className="stat-content">
              <div className="stat-value">{inventory.length}</div>
              <div className="stat-label">Total Products</div>
            </div>
          </div>
          
          <div className="stat-card">
            <div className="stat-icon">
              <FaWarehouse />
            </div>
            <div className="stat-content">
              <div className="stat-value">{inventory.reduce((sum, item) => sum + item.available, 0)}</div>
              <div className="stat-label">Available Items</div>
            </div>
          </div>
          
          <div className="stat-card">
            <div className="stat-icon">
              <FaChartBar />
            </div>
            <div className="stat-content">
              <div className="stat-value">${inventory.reduce((sum, item) => sum + item.totalValue, 0).toLocaleString()}</div>
              <div className="stat-label">Total Value</div>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="content-container">
        {/* Filters */}
        <div className="filters-section">
          <div className="search-bar">
            <FaSearch className="search-icon" />
            <input
              type="text"
              placeholder="Search products..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          
          <div className="filter-controls">
            <div className="filter-group">
              <label>Category</label>
              <select
                value={filterCategory}
                onChange={(e) => setFilterCategory(e.target.value)}
              >
                <option value="all">All Categories</option>
                <option value="Camera">Cameras</option>
                <option value="Lens">Lenses</option>
                <option value="Accessory">Accessories</option>
              </select>
            </div>
            
            <div className="filter-group">
              <label>Sort By</label>
              <select>
                <option>Name</option>
                <option>Category</option>
                <option>Brand</option>
                <option>Quantity</option>
                <option>Available</option>
              </select>
            </div>
          </div>
        </div>

        {/* Table */}
        <div className="table-container">
          <div className="table-header">
            <h2>Product Inventory</h2>
            <div className="table-info">
              Showing {indexOfFirstItem + 1} to {Math.min(indexOfLastItem, filteredInventory.length)} of {filteredInventory.length} items
            </div>
          </div>

          <div className="table-wrapper">
            <table className="inventory-table">
              <thead>
                <tr>
                  <th onClick={() => handleSort('name')}>
                    Product <FaChevronDown className="sort-icon" />
                  </th>
                  <th onClick={() => handleSort('category')}>
                    Category <FaChevronDown className="sort-icon" />
                  </th>
                  <th onClick={() => handleSort('brand')}>
                    Brand <FaChevronDown className="sort-icon" />
                  </th>
                  <th onClick={() => handleSort('quantity')}>
                    Quantity <FaChevronDown className="sort-icon" />
                  </th>
                  <th onClick={() => handleSort('available')}>
                    Available <FaChevronDown className="sort-icon" />
                  </th>
                  <th onClick={() => handleSort('totalValue')}>
                    Value <FaChevronDown className="sort-icon" />
                  </th>
                  <th onClick={() => handleSort('location')}>
                    Location <FaChevronDown className="sort-icon" />
                  </th>
                  <th onClick={() => handleSort('status')}>
                    Status <FaChevronDown className="sort-icon" />
                  </th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {currentItems.map(item => (
                  <tr key={item.id}>
                    <td>
                      <div className="product-info">
                        <div className="product-name">{item.name}</div>
                        <div className="product-model">{item.model}</div>
                      </div>
                    </td>
                    <td>
                      <span className="category-badge">{item.category}</span>
                    </td>
                    <td>{item.brand}</td>
                    <td>{item.quantity}</td>
                    <td>{item.available}</td>
                    <td>${item.totalValue.toLocaleString()}</td>
                    <td>{item.location}</td>
                    <td>
                      <span className={`status-badge status-${getStatusColor(item.status)}`}>
                        {item.status}
                      </span>
                    </td>
                    <td>
                      <div className="action-buttons">
                        <button className="action-btn" title="View">
                          <FaEye />
                        </button>
                        <button className="action-btn" title="Edit">
                          <FaEdit />
                        </button>
                        <button className="action-btn" title="Delete">
                          <FaTrash />
                        </button>
                        <button className="action-btn" title="More">
                          <FaEllipsisV />
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Pagination */}
          {totalPages > 1 && (
            <div className="pagination">
              <button
                className="pagination-btn"
                onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                disabled={currentPage === 1}
              >
                Previous
              </button>
              
              <div className="pagination-numbers">
                {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                  let pageNum;
                  if (totalPages <= 5) {
                    pageNum = i + 1;
                  } else if (currentPage <= 3) {
                    pageNum = i + 1;
                  } else if (currentPage >= totalPages - 2) {
                    pageNum = totalPages - 4 + i;
                  } else {
                    pageNum = currentPage - 2 + i;
                  }
                  
                  return (
                    <button
                      key={pageNum}
                      className={`pagination-number ${currentPage === pageNum ? 'active' : ''}`}
                      onClick={() => setCurrentPage(pageNum)}
                    >
                      {pageNum}
                    </button>
                  );
                })}
              </div>
              
              <button
                className="pagination-btn"
                onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                disabled={currentPage === totalPages}
              >
                Next
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Inventory;
