import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaUser, FaBox, FaCalendarAlt, FaEye, FaArrowLeft, FaArrowRight, FaCheck, FaPlus, FaMinus, FaSearch, FaMapMarkerAlt, FaClock, FaInfoCircle } from 'react-icons/fa';

const NewOrder = () => {
  const [step, setStep] = useState(1); // 1: Customer, 2: Products, 3: Details, 4: Review
  const [customers, setCustomers] = useState([]);
  const [products, setProducts] = useState([]);
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [orderDetails, setOrderDetails] = useState({
    startDate: '',
    endDate: '',
    pickupLocation: '',
    deliveryLocation: '',
    specialInstructions: '',
    total: 0
  });
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredCustomers, setFilteredCustomers] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);

  useEffect(() => {
    loadCustomers();
    loadProducts();
  }, []);

  useEffect(() => {
    // Filter customers based on search term
    if (searchTerm) {
      const filtered = customers.filter(customer =>
        customer.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        customer.email.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredCustomers(filtered);
    } else {
      setFilteredCustomers(customers);
    }
  }, [searchTerm, customers]);

  useEffect(() => {
    // Filter products based on search term
    if (searchTerm) {
      const filtered = products.filter(product =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.brand.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredProducts(filtered);
    } else {
      setFilteredProducts(products);
    }
  }, [searchTerm, products]);

  const loadCustomers = async () => {
    try {
      const response = await axios.get('/customers');
      setCustomers(response.data);
      setFilteredCustomers(response.data);
    } catch (error) {
      console.error('Error loading customers:', error);
    }
  };

  const loadProducts = async () => {
    try {
      const response = await axios.get('/products');
      setProducts(response.data);
      setFilteredProducts(response.data);
    } catch (error) {
      console.error('Error loading products:', error);
    }
  };

  const handleCustomerSelect = (customer) => {
    setSelectedCustomer(customer);
    setStep(2);
  };

  const handleProductSelect = (product) => {
    const existingProduct = selectedProducts.find(p => p.id === product.id);
    if (existingProduct) {
      setSelectedProducts(selectedProducts.filter(p => p.id !== product.id));
    } else {
      setSelectedProducts([...selectedProducts, { ...product, quantity: 1 }]);
    }
  };

  const handleQuantityChange = (productId, quantity) => {
    if (quantity < 1) return;
    setSelectedProducts(selectedProducts.map(p => 
      p.id === productId ? { ...p, quantity } : p
    ));
  };

  const calculateTotal = () => {
    return selectedProducts.reduce((total, product) => {
      return total + (product.price * product.quantity);
    }, 0);
  };

  const handleNextStep = () => {
    if (step < 4) {
      setStep(step + 1);
    }
  };

  const handlePrevStep = () => {
    if (step > 1) {
      setStep(step - 1);
    }
  };

  const handleSubmitOrder = async () => {
    setLoading(true);
    try {
      const orderData = {
        customer_id: selectedCustomer.id,
        products: selectedProducts.map(p => ({
          product_id: p.id,
          quantity: p.quantity
        })),
        start_date: orderDetails.startDate,
        end_date: orderDetails.endDate,
        pickup_location: orderDetails.pickupLocation,
        delivery_location: orderDetails.deliveryLocation,
        special_instructions: orderDetails.specialInstructions,
        total: calculateTotal(),
        status: 'pending'
      };

      const response = await axios.post('/orders', orderData);
      alert('Order created successfully!');
      // Reset form
      setStep(1);
      setSelectedCustomer(null);
      setSelectedProducts([]);
      setOrderDetails({
        startDate: '',
        endDate: '',
        pickupLocation: '',
        deliveryLocation: '',
        specialInstructions: '',
        total: 0
      });
    } catch (error) {
      console.error('Error creating order:', error);
      alert('Error creating order. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const renderStepIndicator = () => (
    <div className="step-indicator">
      <div className="steps">
        <div className={`step ${step >= 1 ? 'active' : ''} ${step > 1 ? 'completed' : ''}`}>
          <div className="step-number">1</div>
          <div className="step-title">Customer</div>
          <div className="step-icon"><FaUser /></div>
        </div>
        <div className={`step ${step >= 2 ? 'active' : ''} ${step > 2 ? 'completed' : ''}`}>
          <div className="step-number">2</div>
          <div className="step-title">Products</div>
          <div className="step-icon"><FaBox /></div>
        </div>
        <div className={`step ${step >= 3 ? 'active' : ''} ${step > 3 ? 'completed' : ''}`}>
          <div className="step-number">3</div>
          <div className="step-title">Details</div>
          <div className="step-icon"><FaCalendarAlt /></div>
        </div>
        <div className={`step ${step >= 4 ? 'active' : ''}`}>
          <div className="step-number">4</div>
          <div className="step-title">Review</div>
          <div className="step-icon"><FaEye /></div>
        </div>
      </div>
      <div className="progress-bar">
        <div className="progress-fill" style={{ width: `${((step - 1) / 3) * 100}%` }}></div>
      </div>
    </div>
  );

  const renderCustomerStep = () => (
    <div className="step-content">
      <div className="step-header">
        <h3>Select Customer</h3>
        <p>Choose an existing customer or create a new one</p>
      </div>
      
      <div className="search-bar">
        <FaSearch className="search-icon" />
        <input
          type="text"
          placeholder="Search customers..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      <div className="customers-grid">
        {filteredCustomers.map(customer => (
          <div 
            key={customer.id} 
            className={`customer-card ${selectedCustomer?.id === customer.id ? 'selected' : ''}`}
            onClick={() => handleCustomerSelect(customer)}
          >
            <div className="customer-avatar">
              <FaUser />
            </div>
            <div className="customer-info">
              <h4>{customer.name}</h4>
              <p>{customer.email}</p>
              <p>{customer.phone || 'No phone number'}</p>
            </div>
            {selectedCustomer?.id === customer.id && (
              <div className="selected-check">
                <FaCheck />
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );

  const renderProductsStep = () => (
    <div className="step-content">
      <div className="step-header">
        <h3>Select Products</h3>
        <p>Choose products to include in this rental order</p>
      </div>

      <div className="search-bar">
        <FaSearch className="search-icon" />
        <input
          type="text"
          placeholder="Search products..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      <div className="products-grid">
        {filteredProducts.map(product => (
          <div key={product.id} className={`product-card ${selectedProducts.find(p => p.id === product.id) ? 'selected' : ''}`}>
            <div className="product-image">
              <div className="product-placeholder">
                <FaBox />
              </div>
            </div>
            <div className="product-info">
              <h4>{product.name}</h4>
              <p className="product-brand">{product.brand}</p>
              <p className="product-price">${product.price}/day</p>
              <p className="product-stock">Stock: {product.stock_quantity}</p>
            </div>
            <button 
              className={`select-btn ${selectedProducts.find(p => p.id === product.id) ? 'selected' : ''}`}
              onClick={() => handleProductSelect(product)}
            >
              {selectedProducts.find(p => p.id === product.id) ? (
                <>
                  <FaCheck /> Selected
                </>
              ) : (
                <>
                  <FaPlus /> Add
                </>
              )}
            </button>
          </div>
        ))}
      </div>
      
      {selectedProducts.length > 0 && (
        <div className="selected-products">
          <h4>Selected Products</h4>
          <div className="selected-items">
            {selectedProducts.map(product => (
              <div key={product.id} className="selected-item">
                <div className="item-info">
                  <span className="item-name">{product.name}</span>
                  <span className="item-price">${product.price}/day</span>
                </div>
                <div className="quantity-controls">
                  <button onClick={() => handleQuantityChange(product.id, product.quantity - 1)}>
                    <FaMinus />
                  </button>
                  <span>{product.quantity}</span>
                  <button onClick={() => handleQuantityChange(product.id, product.quantity + 1)}>
                    <FaPlus />
                  </button>
                </div>
                <div className="item-total">
                  ${product.price * product.quantity}
                </div>
              </div>
            ))}
          </div>
          <div className="order-total">
            <strong>Total: ${calculateTotal()}</strong>
          </div>
        </div>
      )}
    </div>
  );

  const renderDetailsStep = () => (
    <div className="step-content">
      <div className="step-header">
        <h3>Order Details</h3>
        <p>Provide rental period and delivery information</p>
      </div>

      <div className="form-grid">
        <div className="form-group">
          <label>Start Date</label>
          <input 
            type="date" 
            value={orderDetails.startDate}
            onChange={(e) => setOrderDetails({...orderDetails, startDate: e.target.value})}
            required
          />
        </div>
        <div className="form-group">
          <label>End Date</label>
          <input 
            type="date" 
            value={orderDetails.endDate}
            onChange={(e) => setOrderDetails({...orderDetails, endDate: e.target.value})}
            required
          />
        </div>
        <div className="form-group">
          <label>Pickup Location</label>
          <input 
            type="text" 
            value={orderDetails.pickupLocation}
            onChange={(e) => setOrderDetails({...orderDetails, pickupLocation: e.target.value})}
            placeholder="Enter pickup location"
          />
        </div>
        <div className="form-group">
          <label>Delivery Location</label>
          <input 
            type="text" 
            value={orderDetails.deliveryLocation}
            onChange={(e) => setOrderDetails({...orderDetails, deliveryLocation: e.target.value})}
            placeholder="Enter delivery location"
          />
        </div>
        <div className="form-group full-width">
          <label>Special Instructions</label>
          <textarea 
            value={orderDetails.specialInstructions}
            onChange={(e) => setOrderDetails({...orderDetails, specialInstructions: e.target.value})}
            placeholder="Any special instructions..."
            rows={3}
          />
        </div>
      </div>
    </div>
  );

  const renderReviewStep = () => (
    <div className="step-content">
      <div className="step-header">
        <h3>Review Order</h3>
        <p>Review all order details before creating</p>
      </div>

      <div className="review-section">
        <div className="review-card">
          <h4>Customer Information</h4>
          <div className="review-customer">
            <p><strong>Name:</strong> {selectedCustomer.name}</p>
            <p><strong>Email:</strong> {selectedCustomer.email}</p>
            <p><strong>Phone:</strong> {selectedCustomer.phone || 'Not provided'}</p>
          </div>
        </div>
        
        <div className="review-card">
          <h4>Selected Products</h4>
          <div className="review-products">
            {selectedProducts.map(product => (
              <div key={product.id} className="review-product">
                <div className="product-info">
                  <span>{product.name}</span>
                  <span>{product.brand}</span>
                </div>
                <div className="product-quantity">
                  Qty: {product.quantity}
                </div>
                <div className="product-price">
                  ${product.price * product.quantity}
                </div>
              </div>
            ))}
          </div>
        </div>
        
        <div className="review-card">
          <h4>Rental Details</h4>
          <div className="review-details">
            <p><strong>Start Date:</strong> {orderDetails.startDate}</p>
            <p><strong>End Date:</strong> {orderDetails.endDate}</p>
            <p><strong>Pickup Location:</strong> {orderDetails.pickupLocation || 'Not specified'}</p>
            <p><strong>Delivery Location:</strong> {orderDetails.deliveryLocation || 'Not specified'}</p>
            <p><strong>Special Instructions:</strong> {orderDetails.specialInstructions || 'None'}</p>
          </div>
        </div>
        
        <div className="review-total">
          <h3>Total: ${calculateTotal()}</h3>
        </div>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100 font-sans p-8">
      <h2 className="text-3xl font-bold text-gray-900 mb-6">Create New Order</h2>

      <div className="bg-white rounded-xl shadow-lg p-6 mb-6">
        {renderStepIndicator()}
      </div>

      <div className="bg-white rounded-xl shadow-lg overflow-hidden">
        <div className="order-form">
          {step === 1 && renderCustomerStep()}
          {step === 2 && renderProductsStep()}
          {step === 3 && renderDetailsStep()}
          {step === 4 && renderReviewStep()}
          
          <div className="form-navigation">
            <button 
              className="btn btn-secondary" 
              onClick={handlePrevStep}
              disabled={step === 1}
            >
              <FaArrowLeft /> Previous
            </button>
            
            {step < 4 ? (
              <button 
                className="btn btn-primary" 
                onClick={handleNextStep}
                disabled={step === 1 && !selectedCustomer || step === 2 && selectedProducts.length === 0}
              >
                Next <FaArrowRight />
              </button>
            ) : (
              <button 
                className="btn btn-success" 
                onClick={handleSubmitOrder}
                disabled={loading}
              >
                {loading ? 'Creating...' : 'Create Order'}
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default NewOrder;
