import React, { useState, useEffect } from 'react';
import { FaPlus, FaSearch, FaFilter, FaSort, FaEllipsisV, FaEye, FaEdit, FaTrash, FaShoppingCart, FaDollarSign, FaCalendarAlt, FaTruck, FaCheckCircle, FaClock, FaExclamationTriangle, FaChartLine } from 'react-icons/fa';

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [filteredOrders, setFilteredOrders] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [sortConfig, setSortConfig] = useState({ key: 'created_at', direction: 'desc' });
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);

  // Sample orders data
  useEffect(() => {
    const sampleOrders = [
      {
        id: 1,
        customerName: 'John Smith',
        customerEmail: 'john.smith@email.com',
        items: ['Canon EOS R5', '24-70mm Lens'],
        total: 450,
        status: 'completed',
        startDate: '2025-09-01',
        endDate: '2025-09-03',
        pickupLocation: 'Downtown Office',
        deliveryLocation: 'Customer Address',
        createdAt: '2025-09-01 10:30:00'
      },
      {
        id: 2,
        customerName: 'Sarah Johnson',
        customerEmail: 'sarah.j@email.com',
        items: ['Sony A7 III', 'Tripod Pro'],
        total: 180,
        status: 'pending',
        startDate: '2025-09-05',
        endDate: '2025-09-07',
        pickupLocation: 'Warehouse A',
        deliveryLocation: 'Office Building',
        createdAt: '2025-09-02 14:20:00'
      },
      {
        id: 3,
        customerName: 'Mike Davis',
        customerEmail: 'mike.davis@email.com',
        items: ['Nikon Z7 II', '70-200mm Lens'],
        total: 320,
        status: 'in_progress',
        startDate: '2025-09-03',
        endDate: '2025-09-06',
        pickupLocation: 'Warehouse B',
        deliveryLocation: 'Studio Location',
        createdAt: '2025-09-03 09:15:00'
      },
      {
        id: 4,
        customerName: 'Emily Wilson',
        customerEmail: 'emily.w@email.com',
        items: ['Canon EOS R5', 'Flash Speedlite'],
        total: 280,
        status: 'cancelled',
        startDate: '2025-09-08',
        endDate: '2025-09-10',
        pickupLocation: 'Main Office',
        deliveryLocation: 'Residential Address',
        createdAt: '2025-09-04 16:45:00'
      },
      {
        id: 5,
        customerName: 'David Brown',
        customerEmail: 'david.brown@email.com',
        items: ['Sony A7 III', 'Memory Card 128GB'],
        total: 120,
        status: 'completed',
        startDate: '2025-08-28',
        endDate: '2025-08-30',
        pickupLocation: 'Warehouse C',
        deliveryLocation: 'Business Center',
        createdAt: '2025-08-28 11:20:00'
      },
      {
        id: 6,
        customerName: 'Lisa Anderson',
        customerEmail: 'lisa.a@email.com',
        items: ['Canon EOS R5', '24-70mm Lens', 'Tripod Pro'],
        total: 520,
        status: 'in_progress',
        startDate: '2025-09-06',
        endDate: '2025-09-09',
        pickupLocation: 'Downtown Office',
        deliveryLocation: 'Event Venue',
        createdAt: '2025-09-05 13:10:00'
      },
      {
        id: 7,
        customerName: 'Robert Taylor',
        customerEmail: 'robert.t@email.com',
        items: ['Nikon Z7 II'],
        total: 200,
        status: 'pending',
        startDate: '2025-09-10',
        endDate: '2025-09-12',
        pickupLocation: 'Warehouse A',
        deliveryLocation: 'Photography Studio',
        createdAt: '2025-09-06 15:30:00'
      },
      {
        id: 8,
        customerName: 'Jennifer Martinez',
        customerEmail: 'jennifer.m@email.com',
        items: ['Sony A7 III', '70-200mm Lens', 'Flash Speedlite'],
        total: 380,
        status: 'completed',
        startDate: '2025-08-25',
        endDate: '2025-08-27',
        pickupLocation: 'Warehouse B',
        deliveryLocation: 'Hotel Location',
        createdAt: '2025-08-25 08:45:00'
      }
    ];
    setOrders(sampleOrders);
    setFilteredOrders(sampleOrders);
  }, []);

  // Filter and search functionality
  useEffect(() => {
    let filtered = orders;

    if (searchTerm) {
      filtered = filtered.filter(order =>
        order.customerName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        order.customerEmail.toLowerCase().includes(searchTerm.toLowerCase()) ||
        order.items.some(item => item.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }

    if (filterStatus !== 'all') {
      filtered = filtered.filter(order => order.status === filterStatus);
    }

    if (sortConfig.key) {
      filtered.sort((a, b) => {
        const aValue = a[sortConfig.key];
        const bValue = b[sortConfig.key];
        
        if (aValue < bValue) {
          return sortConfig.direction === 'asc' ? -1 : 1;
        }
        if (aValue > bValue) {
          return sortConfig.direction === 'asc' ? 1 : -1;
        }
        return 0;
      });
    }

    setFilteredOrders(filtered);
    setCurrentPage(1);
  }, [searchTerm, filterStatus, sortConfig, orders]);

  const handleSort = (key) => {
    let direction = 'asc';
    if (sortConfig.key === key && sortConfig.direction === 'asc') {
      direction = 'desc';
    }
    setSortConfig({ key, direction });
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed':
        return 'success';
      case 'pending':
        return 'warning';
      case 'in_progress':
        return 'info';
      case 'cancelled':
        return 'danger';
      default:
        return 'secondary';
    }
  };

  const getStatusBadgeColor = (status) => {
    switch (status) {
      case 'completed':
        return 'rgba(56, 224, 123, 0.1)';
      case 'pending':
        return 'rgba(255, 165, 0, 0.1)';
      case 'in_progress':
        return 'rgba(52, 152, 219, 0.1)';
      case 'cancelled':
        return 'rgba(255, 99, 71, 0.1)';
      default:
        return 'rgba(150, 197, 169, 0.1)';
    }
  };

  const getStatusTextColor = (status) => {
    switch (status) {
      case 'completed':
        return '#38e07b';
      case 'pending':
        return '#ffa500';
      case 'in_progress':
        return '#3498db';
      case 'cancelled':
        return '#ff6347';
      default:
        return '#96c5a9';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'completed':
        return <FaCheckCircle />;
      case 'pending':
        return <FaClock />;
      case 'in_progress':
        return <FaTruck />;
      case 'cancelled':
        return <FaExclamationTriangle />;
      default:
        return <FaClock />;
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'completed':
        return 'Completed';
      case 'pending':
        return 'Pending';
      case 'in_progress':
        return 'In Progress';
      case 'cancelled':
        return 'Cancelled';
      default:
        return status;
    }
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentOrders = filteredOrders.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(filteredOrders.length / itemsPerPage);

  return (
    <div className="orders-page">
      {/* Header */}
      <div className="content-header">
        <div className="header-left">
          <h1>Orders Management</h1>
          <p>Manage customer rental orders and track order status</p>
        </div>
        <div className="header-actions">
          <button className="btn btn-primary" onClick={() => console.log('New Order')}>
            <FaPlus /> New Order
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="page-content">
        {/* Stats Cards */}
        <div className="stats-grid">
          <div className="stat-card primary">
            <div className="stat-icon">
              <FaShoppingCart />
            </div>
            <div className="stat-content">
              <div className="stat-value">{orders.length}</div>
              <div className="stat-label">Total Orders</div>
              <div className="stat-trend">
                <FaChartLine className="trend-up" />
                <span>12% from last month</span>
              </div>
            </div>
          </div>
          
          <div className="stat-card success">
            <div className="stat-icon">
              <FaDollarSign />
            </div>
            <div className="stat-content">
              <div className="stat-value">${orders.reduce((sum, order) => sum + order.total, 0).toLocaleString()}</div>
              <div className="stat-label">Total Revenue</div>
              <div className="stat-trend">
                <FaChartLine className="trend-up" />
                <span>8% from last month</span>
              </div>
            </div>
          </div>
          
          <div className="stat-card warning">
            <div className="stat-icon">
              <FaClock />
            </div>
            <div className="stat-content">
              <div className="stat-value">{orders.filter(order => order.status === 'pending').length}</div>
              <div className="stat-label">Pending Orders</div>
              <div className="stat-trend">
                <FaChartLine className="trend-down" />
                <span>5% from last month</span>
              </div>
            </div>
          </div>
          
          <div className="stat-card info">
            <div className="stat-icon">
              <FaTruck />
            </div>
            <div className="stat-content">
              <div className="stat-value">{orders.filter(order => order.status === 'in_progress').length}</div>
              <div className="stat-label">In Progress</div>
              <div className="stat-trend">
                <FaChartLine className="trend-stable" />
                <span>Stable</span>
              </div>
            </div>
          </div>
        </div>

        {/* Search and Filters */}
        <div className="controls-section">
          <div className="search-container">
            <div className="search-bar">
              <FaSearch className="search-icon" />
              <input
                type="text"
                placeholder="Search orders..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
          </div>
          
          <div className="filters-container">
            <div className="filter-group">
              <label>Status</label>
              <select
                value={filterStatus}
                onChange={(e) => setFilterStatus(e.target.value)}
              >
                <option value="all">All Status</option>
                <option value="pending">Pending</option>
                <option value="in_progress">In Progress</option>
                <option value="completed">Completed</option>
                <option value="cancelled">Cancelled</option>
              </select>
            </div>
            
            <div className="filter-group">
              <label>Sort By</label>
              <select>
                <option>Date Created</option>
                <option>Customer Name</option>
                <option>Total Amount</option>
                <option>Status</option>
              </select>
            </div>
          </div>
        </div>

        {/* Orders Table */}
        <div className="table-section">
          <div className="table-header">
            <h3>Orders List</h3>
            <div className="table-actions">
              <span className="results-count">
                Showing {currentOrders.length} of {filteredOrders.length} orders
              </span>
            </div>
          </div>

          <div className="table-container">
            <table className="orders-table">
              <thead>
                <tr>
                  <th onClick={() => handleSort('id')}>
                    Order ID <FaSort className="sort-icon" />
                  </th>
                  <th onClick={() => handleSort('customerName')}>
                    Customer <FaSort className="sort-icon" />
                  </th>
                  <th onClick={() => handleSort('items')}>
                    Items <FaSort className="sort-icon" />
                  </th>
                  <th onClick={() => handleSort('total')}>
                    Total <FaSort className="sort-icon" />
                  </th>
                  <th onClick={() => handleSort('status')}>
                    Status <FaSort className="sort-icon" />
                  </th>
                  <th onClick={() => handleSort('startDate')}>
                    Start Date <FaSort className="sort-icon" />
                  </th>
                  <th onClick={() => handleSort('endDate')}>
                    End Date <FaSort className="sort-icon" />
                  </th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {currentOrders.map(order => (
                  <tr key={order.id}>
                    <td>
                      <div className="order-id">
                        #{order.id}
                      </div>
                    </td>
                    <td>
                      <div className="customer-info">
                        <div className="customer-name">{order.customerName}</div>
                        <div className="customer-email">{order.customerEmail}</div>
                      </div>
                    </td>
                    <td>
                      <div className="items-list">
                        {order.items.map((item, index) => (
                          <span key={index} className="item-tag">
                            {item}
                            {index < order.items.length - 1 && ', '}
                          </span>
                        ))}
                      </div>
                    </td>
                    <td>
                      <div className="order-total">
                        ${order.total.toLocaleString()}
                      </div>
                    </td>
                    <td>
                      <span 
                        className={`status-badge status-${getStatusColor(order.status)}`} 
                        style={{ 
                          backgroundColor: getStatusBadgeColor(order.status),
                          color: getStatusTextColor(order.status)
                        }}
                      >
                        <span className="status-icon">{getStatusIcon(order.status)}</span>
                        {getStatusText(order.status)}
                      </span>
                    </td>
                    <td>
                      <div className="date-cell">
                        {new Date(order.startDate).toLocaleDateString()}
                      </div>
                    </td>
                    <td>
                      <div className="date-cell">
                        {new Date(order.endDate).toLocaleDateString()}
                      </div>
                    </td>
                    <td>
                      <div className="action-buttons">
                        <button className="action-btn" title="View" onClick={() => console.log('View', order.id)}>
                          <FaEye />
                        </button>
                        <button className="action-btn" title="Edit" onClick={() => console.log('Edit', order.id)}>
                          <FaEdit />
                        </button>
                        <button className="action-btn" title="Delete" onClick={() => console.log('Delete', order.id)}>
                          <FaTrash />
                        </button>
                        <button className="action-btn" title="More" onClick={() => console.log('More', order.id)}>
                          <FaEllipsisV />
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            {/* Pagination */}
            {totalPages > 1 && (
              <div className="pagination">
                <button
                  className="pagination-btn"
                  onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                  disabled={currentPage === 1}
                >
                  Previous
                </button>
                
                <div className="pagination-numbers">
                  {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                    let pageNum;
                    if (totalPages <= 5) {
                      pageNum = i + 1;
                    } else if (currentPage <= 3) {
                      pageNum = i + 1;
                    } else if (currentPage >= totalPages - 2) {
                      pageNum = totalPages - 4 + i;
                    } else {
                      pageNum = currentPage - 2 + i;
                    }
                    
                    return (
                      <button
                        key={pageNum}
                        className={`pagination-number ${currentPage === pageNum ? 'active' : ''}`}
                        onClick={() => setCurrentPage(pageNum)}
                      >
                        {pageNum}
                      </button>
                    );
                  })}
                </div>
                
                <button
                  className="pagination-btn"
                  onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                  disabled={currentPage === totalPages}
                >
                  Next
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Orders;
