import React, { useState, useEffect } from 'react';
import { FaCalendarAlt, FaChevronLeft, FaChevronRight, FaPlus, FaSearch, FaFilter } from 'react-icons/fa';

const Calendar = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [events, setEvents] = useState([]);
  const [view, setView] = useState('month'); // month, week, day

  // Sample events data
  useEffect(() => {
    const sampleEvents = [
      {
        id: 1,
        title: 'Camera Rental - John Doe',
        date: new Date(2025, 8, 15),
        startTime: '09:00',
        endTime: '17:00',
        type: 'rental',
        customer: 'John Doe',
        product: 'Canon EOS R5'
      },
      {
        id: 2,
        title: 'Lenses Package - Jane Smith',
        date: new Date(2025, 8, 16),
        startTime: '10:00',
        endTime: '18:00',
        type: 'rental',
        customer: 'Jane Smith',
        product: 'Lens Package'
      },
      {
        id: 3,
        title: 'Studio Equipment - Mike Johnson',
        date: new Date(2025, 8, 17),
        startTime: '08:00',
        endTime: '20:00',
        type: 'rental',
        customer: 'Mike Johnson',
        product: 'Studio Lighting Kit'
      },
      {
        id: 4,
        title: 'Maintenance - Camera Cleaning',
        date: new Date(2025, 8, 18),
        startTime: '09:00',
        endTime: '12:00',
        type: 'maintenance',
        customer: 'Internal',
        product: 'All Cameras'
      }
    ];
    setEvents(sampleEvents);
  }, []);

  const getDaysInMonth = (year, month) => {
    return new Date(year, month + 1, 0).getDate();
  };

  const getFirstDayOfMonth = (year, month) => {
    return new Date(year, month, 1).getDay();
  };

  const getMonthName = (date) => {
    return date.toLocaleDateString('en-US', { month: 'long', year: 'numeric' });
  };

  const getEventsForDate = (date) => {
    return events.filter(event => {
      const eventDate = new Date(event.date);
      return eventDate.toDateString() === date.toDateString();
    });
  };

  const renderMonthView = () => {
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth();
    const daysInMonth = getDaysInMonth(year, month);
    const firstDay = getFirstDayOfMonth(year, month);
    
    const days = [];
    
    // Empty cells for days before month starts
    for (let i = 0; i < firstDay; i++) {
      days.push(<div key={`empty-${i}`} className="calendar-day empty"></div>);
    }
    
    // Days of the month
    for (let day = 1; day <= daysInMonth; day++) {
      const date = new Date(year, month, day);
      const dayEvents = getEventsForDate(date);
      const isToday = date.toDateString() === new Date().toDateString();
      const isSelected = date.toDateString() === selectedDate.toDateString();
      
      days.push(
        <div 
          key={`day-${day}`} 
          className={`calendar-day ${isToday ? 'today' : ''} ${isSelected ? 'selected' : ''}`}
          onClick={() => setSelectedDate(date)}
        >
          <div className="day-number">{day}</div>
          <div className="day-events">
            {dayEvents.slice(0, 3).map(event => (
              <div key={event.id} className={`event-dot ${event.type}`}>
                <span className="event-tooltip">{event.title}</span>
              </div>
            ))}
            {dayEvents.length > 3 && (
              <div className="event-more">+{dayEvents.length - 3} more</div>
            )}
          </div>
        </div>
      );
    }
    
    return days;
  };

  const renderWeekView = () => {
    const startOfWeek = new Date(selectedDate);
    startOfWeek.setDate(selectedDate.getDate() - selectedDate.getDay());
    
    const weekDays = [];
    for (let i = 0; i < 7; i++) {
      const date = new Date(startOfWeek);
      date.setDate(startOfWeek.getDate() + i);
      const dayEvents = getEventsForDate(date);
      
      weekDays.push(
        <div key={`week-${i}`} className="calendar-week-day">
          <div className="week-day-header">
            <div className="week-day-name">{date.toLocaleDateString('en-US', { weekday: 'short' })}</div>
            <div className="week-day-number">{date.getDate()}</div>
          </div>
          <div className="week-day-events">
            {dayEvents.map(event => (
              <div key={event.id} className={`week-event ${event.type}`}>
                <div className="event-time">{event.startTime}</div>
                <div className="event-title">{event.title}</div>
              </div>
            ))}
          </div>
        </div>
      );
    }
    
    return weekDays;
  };

  const renderDayView = () => {
    const dayEvents = getEventsForDate(selectedDate);
    
    return (
      <div className="calendar-day-view">
        <div className="day-view-header">
          <h3>{selectedDate.toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</h3>
        </div>
        <div className="day-view-events">
          {dayEvents.length === 0 ? (
            <div className="no-events">No events scheduled for this day</div>
          ) : (
            dayEvents.map(event => (
              <div key={event.id} className={`day-event ${event.type}`}>
                <div className="event-time">{event.startTime} - {event.endTime}</div>
                <div className="event-details">
                  <div className="event-title">{event.title}</div>
                  <div className="event-info">
                    <span>Customer: {event.customer}</span>
                    <span>Product: {event.product}</span>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    );
  };

  const navigateMonth = (direction) => {
    const newDate = new Date(currentDate);
    newDate.setMonth(currentDate.getMonth() + direction);
    setCurrentDate(newDate);
  };

  return (
    <div className="calendar-page">
      <div className="calendar-header">
        <div className="calendar-title">
          <FaCalendarAlt className="calendar-icon" />
          <h1>Calendar</h1>
        </div>
        
        <div className="calendar-controls">
          <div className="view-switcher">
            <button 
              className={`view-btn ${view === 'month' ? 'active' : ''}`}
              onClick={() => setView('month')}
            >
              Month
            </button>
            <button 
              className={`view-btn ${view === 'week' ? 'active' : ''}`}
              onClick={() => setView('week')}
            >
              Week
            </button>
            <button 
              className={`view-btn ${view === 'day' ? 'active' : ''}`}
              onClick={() => setView('day')}
            >
              Day
            </button>
          </div>
          
          <div className="calendar-nav">
            <button className="nav-btn" onClick={() => navigateMonth(-1)}>
              <FaChevronLeft />
            </button>
            <h2 className="current-month">{getMonthName(currentDate)}</h2>
            <button className="nav-btn" onClick={() => navigateMonth(1)}>
              <FaChevronRight />
            </button>
          </div>
          
          <div className="calendar-actions">
            <button className="action-btn">
              <FaPlus /> New Event
            </button>
            <button className="action-btn">
              <FaSearch /> Search
            </button>
            <button className="action-btn">
              <FaFilter /> Filter
            </button>
          </div>
        </div>
      </div>

      <div className="calendar-content">
        {view === 'month' && (
          <div className="calendar-month-view">
            <div className="calendar-weekdays">
              {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
                <div key={day} className="calendar-weekday">{day}</div>
              ))}
            </div>
            <div className="calendar-days">
              {renderMonthView()}
            </div>
          </div>
        )}
        
        {view === 'week' && (
          <div className="calendar-week-view">
            {renderWeekView()}
          </div>
        )}
        
        {view === 'day' && (
          <div className="calendar-day-container">
            {renderDayView()}
          </div>
        )}
      </div>

      <div className="calendar-legend">
        <h3>Event Types</h3>
        <div className="legend-items">
          <div className="legend-item">
            <div className="legend-dot rental"></div>
            <span>Rental</span>
          </div>
          <div className="legend-item">
            <div className="legend-dot maintenance"></div>
            <span>Maintenance</span>
          </div>
          <div className="legend-item">
            <div className="legend-dot delivery"></div>
            <span>Delivery</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Calendar;
