import React, { useState, useEffect } from 'react';
import { 
  FaPlus, FaSearch, FaFilter, FaSort, FaEllipsisV, FaEye, FaEdit, FaTrash, 
  FaShoppingCart, FaDollarSign, FaCalendarAlt, FaTruck, FaCheckCircle, 
  FaClock, FaExclamationTriangle, FaChartLine, FaDownload, FaUpload, FaCog,
  FaBell, FaUser, FaBuilding, faChartBar, faChartLine, faChartPie,
  faTable, faList, faTh, faChevronDown, faChevronUp, faTimes, faBars
} from 'react-icons/fa';

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [filteredOrders, setFilteredOrders] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [sortConfig, setSortConfig] = useState({ key: 'created_at', direction: 'desc' });
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);
  const [viewMode, setViewMode] = useState('table'); // table, grid, card
  const [selectedOrders, setSelectedOrders] = useState([]);
  const [showFilters, setShowFilters] = useState(false);
  const [loading, setLoading] = useState(false);

  // Sample orders data with more details
  useEffect(() => {
    setLoading(true);
    const sampleOrders = [
      {
        id: 1,
        customerName: 'John Smith',
        customerEmail: 'john.smith@email.com',
        customerPhone: '+1 (555) 123-4567',
        company: 'TechCorp Inc.',
        items: [
          { name: 'Canon EOS R5', quantity: 1, dailyRate: 150, duration: 2 },
          { name: '24-70mm f/2.8 Lens', quantity: 1, dailyRate: 50, duration: 2 }
        ],
        total: 400,
        subtotal: 400,
        tax: 0,
        discount: 0,
        status: 'completed',
        priority: 'high',
        startDate: '2025-09-01',
        endDate: '2025-09-03',
        pickupLocation: 'Downtown Office',
        deliveryLocation: '123 Main St, New York, NY',
        createdAt: '2025-09-01 10:30:00',
        updatedAt: '2025-09-03 14:20:00',
        assignedTo: 'Sarah Johnson',
        notes: 'Client requested equipment by 9 AM'
      },
      {
        id: 2,
        customerName: 'Sarah Johnson',
        customerEmail: 'sarah.j@email.com',
        customerPhone: '+1 (555) 987-6543',
        company: 'Creative Studios',
        items: [
          { name: 'Sony A7 III', quantity: 1, dailyRate: 120, duration: 2 },
          { name: 'Tripod Pro', quantity: 1, dailyRate: 30, duration: 2 }
        ],
        total: 300,
        subtotal: 300,
        tax: 0,
        discount: 0,
        status: 'pending',
        priority: 'medium',
        startDate: '2025-09-05',
        endDate: '2025-09-07',
        pickupLocation: 'Warehouse A',
        deliveryLocation: '456 Oak Ave, Los Angeles, CA',
