import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { getProducts as apiGetProducts, getCategories } from '../services/api';
import { toast } from 'react-toastify';
import './Products.css';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const initialSearch = params.get('search') || '';
  const [searchTerm, setSearchTerm] = useState(initialSearch);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [category, setCategory] = useState('All Equipment');
  const navigate = useNavigate();
  const [categoryOptions, setCategoryOptions] = useState(['All Equipment']);

  useEffect(() => {
    // Set fallback products immediately for presentation
    const fallbackProducts = [
      {
        id: 1,
        name: "CAT 320 Excavator",
        description: "Heavy-duty excavator perfect for digging, trenching, and construction work. Features advanced hydraulics and operator comfort.",
        sku: "EXC-320",
        category: "Excavator",
        published: true
      },
      {
        id: 2,
        name: "Mack Granite Dump Truck", 
        description: "Reliable dump truck for hauling materials, debris, and aggregate. Features a 15-yard capacity and excellent maneuverability.",
        sku: "DMP-001",
        category: "Dump Truck",
        published: true
      },
      {
        id: 3,
        name: "John Deere 410L Backhoe Loader",
        description: "Versatile backhoe loader combining loading and digging capabilities. Perfect for utility work and site preparation.",
        sku: "BCK-410",
        category: "Backhoe Loader", 
        published: true
      },
      {
        id: 4,
        name: "CAT 950M Wheel Loader",
        description: "Powerful wheel loader designed for heavy material handling, loading trucks, and stockpile management.",
        sku: "WHL-950",
        category: "Wheel Loader",
        published: true
      },
      {
        id: 5,
        name: "Grove RT890E Rough Terrain Crane",
        description: "90-ton rough terrain crane ideal for construction sites with limited access. Features excellent mobility and lifting capacity.",
        sku: "CRN-890",
        category: "Crane",
        published: true
      },
      {
        id: 6,
        name: "CAT D6T Bulldozer",
        description: "Medium bulldozer perfect for earthmoving, grading, and site preparation. Features excellent fuel efficiency.",
        sku: "BLD-D6T",
        category: "Bulldozer",
        published: true
      }
    ];

    setProducts(fallbackProducts);
    loadProducts();
    loadCategories();
  }, []);

  useEffect(() => {
    // Filter products based on search term
    const filtered = products.filter(product => {
      const inSearch =
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.sku.toLowerCase().includes(searchTerm.toLowerCase());
      const cat = getCategory(product);
      const inCategory = category === 'All Equipment' || cat === category;
      return inSearch && inCategory;
    });
    setFilteredProducts(filtered);
  }, [products, searchTerm, category]);

  const loadProducts = async () => {
    try {
      const response = await apiGetProducts();
      setProducts(response.data || []);
    } catch (error) {
      console.error('Error loading products:', error);
      toast.error('Failed to load products');
    } finally {
      setLoading(false);
    }
  };

  const loadCategories = async () => {
    try {
      const res = await getCategories();
      const serverCats = Array.isArray(res.data) ? res.data : [];
      setCategoryOptions(['All Equipment', ...serverCats]);
    } catch (e) {
      console.error('Failed to load categories', e);
    }
  };

  const getCategory = (p) => p.category || 'Equipment';
  const getImage = (p) => p.image_url || '';

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  // React to URL query param changes (e.g., from header search)
  useEffect(() => {
    const p = new URLSearchParams(location.search);
    const s = p.get('search') || '';
    setSearchTerm(s);
  }, [location.search]);

  const categories = categoryOptions;

  if (loading) {
    return (
      <div className="products-container">
        <div className="products-header">
          <h1>Equipment Catalog</h1>
          <p>Loading equipment...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="equipment-catalog">
      <div className="catalog-header">
        <h1>Equipment Catalog</h1>
        <p>Discover our comprehensive range of construction equipment</p>

        <div className="filter-container">
          <select
            className="filter-select"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          >
            {categoryOptions.map(cat => (
              <option key={cat} value={cat}>{cat}</option>
            ))}
          </select>
        </div>
      </div>

      {filteredProducts.length > 0 ? (
        <div className="equipment-grid">
          {filteredProducts.map(product => (
            <div key={product.id} className="equipment-card">
              <div className="equipment-image">
                {getImage(product) ? (
                  <img src={getImage(product)} alt={product.name} />
                ) : (
                  <div className="equipment-placeholder">
                    <div className="equipment-type">{getCategory(product)}</div>
                  </div>
                )}
              </div>
              <div className="equipment-info">
                <h3>{product.name}</h3>
                <p className="equipment-description">{product.description}</p>
                <div className="equipment-details">
                  <div className="detail-row">
                    <span className="label">Available:</span>
                    <span className="value">In Stock</span>
                  </div>
                  <div className="detail-row">
                    <span className="label">Location:</span>
                    <span className="value">Main Yard</span>
                  </div>
                </div>
                <button className="book-btn">Book Now</button>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="no-equipment">
          <h3>No equipment found</h3>
          <p>{searchTerm ? 'Try adjusting your search terms' : 'No equipment available at the moment'}</p>
        </div>
      )}
    </div>
  );
};

export default Products;
