import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';
import { FaUser, FaBars, FaTimes, FaSearch } from 'react-icons/fa';
import './Header.css';

const Header = () => {
  const { user, logout, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);
  const [query, setQuery] = useState('');

  const handleLogout = () => {
    logout();
    navigate('/');
    setMenuOpen(false);
  };

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const closeMenu = () => {
    setMenuOpen(false);
  };

  return (
    <header className="header">
      <div className="header-container">
        <div className="header-left">
          <Link to="/" className="brand" onClick={closeMenu}>
            <img src="/rslaf-logo.png" alt="RSLAF Rentals" onError={(e) => { e.currentTarget.style.display = 'none'; }} />
            <span>RSLAF Rentals</span>
          </Link>
          <nav className={`header-nav ${menuOpen ? 'open' : ''}`}>
            <Link to="/" onClick={closeMenu}>Home</Link>
            <Link to="/products" onClick={closeMenu}>All Machines</Link>
            <Link to="/about" onClick={closeMenu}>About Us</Link>
          </nav>
        </div>

        <div className="nav-search">
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="What are you looking for?"
          />
          <button onClick={() => navigate(`/products?search=${encodeURIComponent(query)}`)} aria-label="Search">
            <FaSearch />
          </button>
        </div>

        <div className="header-actions">
          {isAuthenticated ? (
            <>
              <Link to="/orders" onClick={closeMenu}>My Bookings</Link>
              <Link to="/profile" onClick={closeMenu} className="profile-link">
                <FaUser /> Profile
              </Link>
              <button onClick={handleLogout} className="logout-btn">Logout</button>
            </>
          ) : (
            <>
              <Link to="/login" onClick={closeMenu}>Login</Link>
            </>
          )}
          <button className="menu-toggle" onClick={toggleMenu}>
            {menuOpen ? <FaTimes /> : <FaBars />}
          </button>
        </div>
      </div>

      {isAuthenticated && (
        <div className="user-info">
          <span>Welcome, {user?.email}</span>
        </div>
      )}
    </header>
  );
};

export default Header;
