import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { getProducts as apiGetProducts, getCategories } from '../services/api';
import { toast } from 'react-toastify';
import './Products.css';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const initialSearch = params.get('search') || '';
  const [searchTerm, setSearchTerm] = useState(initialSearch);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [category, setCategory] = useState('All Equipment');
  const navigate = useNavigate();
  const [categoryOptions, setCategoryOptions] = useState(['All Equipment']);

  useEffect(() => {
    // Load products from API first, then fallback to sample data
    loadProducts();
    loadCategories();
  }, []);

  const loadProducts = async () => {
    try {
      const response = await fetch('http://localhost:8000/products?published_only=true');
      if (response.ok) {
        const apiProducts = await response.json();
        if (apiProducts && apiProducts.length > 0) {
          setProducts(apiProducts);
          return;
        }
      }
    } catch (error) {
      console.log('API not available, using fallback data');
    }

    // Fallback to sample data if API fails
    const constructionEquipment = [
      {
        id: 1,
        name: "CAT 320 Excavator",
        description: "Heavy-duty excavator perfect for digging, trenching, and demolition work. Features advanced hydraulics and operator comfort systems for maximum productivity.",
        sku: "EXC-320",
        category: "Excavator",
        available: 3,
        location: "Downtown Yard",
        hourly_rate: 450,
        specifications: "Operating Weight: 20,300 kg | Max Dig Depth: 6.7m | Engine Power: 122 kW",
        image_url: "https://images.unsplash.com/photo-1581094794329-c8112a89af12?w=600&h=400&fit=crop"
      },
      {
        id: 2,
        name: "Mack Granite Dump Truck", 
        description: "Reliable dump truck for hauling materials, debris, and aggregate. Features a 15-yard capacity with excellent maneuverability and fuel efficiency.",
        sku: "DMP-001",
        category: "Dump Truck",
        available: 5,
        location: "North Yard",
        hourly_rate: 380,
        specifications: "Payload: 15 cubic yards | GVW: 33,000 lbs | Engine: 405 HP Mack MP8",
        image_url: "https://images.unsplash.com/photo-1558618666-fcd25c85cd64?w=600&h=400&fit=crop"
      },
      {
        id: 3,
        name: "John Deere 410L Backhoe Loader",
        description: "Versatile backhoe loader combining loading and digging capabilities. Perfect for utility work, site preparation, and material handling operations.",
        sku: "BCK-410",
        category: "Backhoe Loader", 
        available: 4,
        location: "Central Yard",
        hourly_rate: 320,
        specifications: "Dig Depth: 4.3m | Loader Capacity: 1.0 m¬≥ | Engine: 74 kW Tier 4",
        image_url: "https://images.unsplash.com/photo-1572981779307-38b8cabb2407?w=600&h=400&fit=crop"
      },
      {
        id: 4,
        name: "CAT 950M Wheel Loader",
        description: "Powerful wheel loader designed for heavy material handling, loading trucks, and stockpile management. Built for durability and performance.",
        sku: "WHL-950",
        category: "Wheel Loader",
        available: 2,
