import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import './Products.css';

  // Client-side demo mapping for images and categories (no backend changes required)
  const demoMap = {
    'EXC-CAT320-001': { category: 'Excavator', image: 'https://images.unsplash.com/photo-1616401784845-c97df2ce2e94?q=80&w=1200&auto=format&fit=crop' },
    'DMP-MACK-GRN-001': { category: 'Dump Truck', image: 'https://images.unsplash.com/photo-1635322160730-70a87dcc2069?q=80&w=1200&auto=format&fit=crop' },
    'BHL-JD-410L-001': { category: 'Backhoe Loader', image: 'https://images.unsplash.com/photo-1570126646281-5ec88111777d?q=80&w=1200&auto=format&fit=crop' },
    'WHL-CAT950M-001': { category: 'Wheel Loader', image: 'https://images.unsplash.com/photo-1581092921461-eab62e97a780?q=80&w=1200&auto=format&fit=crop' },
    'CRN-GRV-RT890E-001': { category: 'Crane', image: 'https://images.unsplash.com/photo-1541976076758-347942db197a?q=80&w=1200&auto=format&fit=crop' },
    'BLD-CAT-D6T-001': { category: 'Bulldozer', image: 'https://images.unsplash.com/photo-1574201635302-388dd92a4c22?q=80&w=1200&auto=format&fit=crop' },
  };

  const getCategory = (p) => p.category || p.type || demoMap[p.sku]?.category || 'Equipment';
  const getImage = (p) => p.image_url || p.image || p.thumbnail_url || demoMap[p.sku]?.image || '';
const Products = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const initialSearch = params.get('search') || '';
  const [searchTerm, setSearchTerm] = useState(initialSearch);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [category, setCategory] = useState('All Equipment');
  const navigate = useNavigate();

  useEffect(() => {
    loadProducts();
  }, []);

  useEffect(() => {
    // Filter products based on search term
    const filtered = products.filter(product => {
      const inSearch =
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.sku.toLowerCase().includes(searchTerm.toLowerCase());
      const cat = getCategory(product);
      const inCategory = category === 'All Equipment' || cat === category;
      return inSearch && inCategory;
    });
    setFilteredProducts(filtered);
  }, [products, searchTerm, category]);

  const loadProducts = async () => {
    try {
      const response = await axios.get('/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error loading products:', error);
      toast.error('Failed to load products');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  // React to URL query param changes (e.g., from header search)
  useEffect(() => {
    const p = new URLSearchParams(location.search);
    const s = p.get('search') || '';
    setSearchTerm(s);
  }, [location.search]);

  const categories = ['All Equipment', ...Array.from(new Set(products.map(p => getCategory(p))))];

  if (loading) {
    return (
      <div className="products-container">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading products...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="products-container">
      <div className="container">
        <div className="products-header">
          <h1>Equipment Catalog</h1>
          <p>Discover our comprehensive range of construction equipment</p>

          {/* Search Bar */}
          <div className="search-container">
            <input
              type="text"
              placeholder="Search products by name, description, or SKU..."
              value={searchTerm}
              onChange={handleSearch}
              className="search-input"
            />
            <button className="search-btn">🔍</button>
          </div>



          {/* Category Filter */}
          <div className="filter-container">
            <select
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              className="filter-select"
            >
              {categories.map((c) => (
                <option key={c} value={c}>{c}</option>
              ))}
            </select>
          </div>
        </div>

        {filteredProducts.length > 0 ? (
          <div className="products-grid">
            {filteredProducts.map(product => (
              <div key={product.id} className="product-card" style={{ cursor: 'pointer' }} onClick={() => navigate(`/products/${product.id}`)}>
                <div className="product-image">
                  { getImage(product) ? (
                    <img src={getImage(product)} alt={product.name} style={{ width: '100%', height: '100%', objectFit: 'cover' }} onError={(e)=>{ e.currentTarget.style.display='none'; }} />
                  ) : (
                    <div className="product-placeholder">
                      {product.name.charAt(0)}
                    </div>
                  )}
                </div>
                <div className="product-info">
                  <h3>{product.name}</h3>
                  <p className="product-sku">SKU: {product.sku}</p>
                  <p className="product-description">{product.description}</p>
                  <div className="product-meta">
                    <span className="badge badge-category">{getCategory(product)}</span>
                  </div>
                  <Link to={`/products/${product.id}`} className="btn btn-outline">
                    View Details
                  </Link>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="no-products">
            <h3>No products found</h3>
            <p>{searchTerm ? 'Try adjusting your search terms' : 'No products available at the moment'}</p>
            {searchTerm && (
              <button
                onClick={() => setSearchTerm('')}
                className="btn btn-secondary"
              >
                Clear Search
              </button>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Products;
