import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { getProducts as apiGetProducts, getCategories } from '../services/api';
import { toast } from 'react-toastify';
import './Products.css';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const initialSearch = params.get('search') || '';
  const [searchTerm, setSearchTerm] = useState(initialSearch);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [category, setCategory] = useState('All Equipment');
  const navigate = useNavigate();
  const [categoryOptions, setCategoryOptions] = useState(['All Equipment']);

  useEffect(() => {
    // Set default products immediately
    const defaultProducts = [
      {
        id: 1,
        name: "Excavator CAT 320",
        description: "Heavy-duty excavator perfect for construction projects",
        sku: "EXC-320",
        category: "Heavy Equipment",
        daily_rate: 450,
        image_url: null
      },
      {
        id: 2,
        name: "Power Drill Set",
        description: "Professional power drill with multiple attachments",
        sku: "DRL-001",
        category: "Power Tools",
        daily_rate: 25,
        image_url: null
      },
      {
        id: 3,
        name: "Concrete Mixer",
        description: "Portable concrete mixer for medium construction jobs",
        sku: "MIX-450",
        category: "Heavy Equipment",
        daily_rate: 180,
        image_url: null
      },
      {
        id: 4,
        name: "Generator 5000W",
        description: "Reliable portable generator for power needs",
        sku: "GEN-5000",
        category: "Power Equipment",
        daily_rate: 75,
        image_url: null
      }
    ];
    setProducts(defaultProducts);
    loadProducts();
    loadCategories();
  }, []);

  useEffect(() => {
    // Filter products based on search term
    const filtered = products.filter(product => {
      const inSearch =
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.sku.toLowerCase().includes(searchTerm.toLowerCase());
      const cat = getCategory(product);
      const inCategory = category === 'All Equipment' || cat === category;
      return inSearch && inCategory;
    });
    setFilteredProducts(filtered);
  }, [products, searchTerm, category]);

  const loadProducts = async () => {
    try {
      const response = await apiGetProducts({ published_only: true });
      if (response.data && response.data.length > 0) {
        setProducts(response.data);
      }
    } catch (error) {
      console.error('Error loading products:', error);
      // Keep default products on error
    }
  };

  const loadCategories = async () => {
    try {
      const res = await getCategories();
      const serverCats = Array.isArray(res.data) ? res.data : [];
      setCategoryOptions(['All Equipment', ...serverCats]);
    } catch (e) {
      console.error('Failed to load categories', e);
    }
  };

  const getCategory = (p) => p.category || 'Equipment';
  const getImage = (p) => p.image_url || '';

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  // React to URL query param changes (e.g., from header search)
  useEffect(() => {
    const p = new URLSearchParams(location.search);
    const s = p.get('search') || '';
    setSearchTerm(s);
  }, [location.search]);

  const categories = categoryOptions;

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="products-container">
      <div className="products-header">
        <h1>Equipment Rentals</h1>
        <p>Find the perfect equipment for your project</p>

        <div className="search-container">
          <input
            type="text"
            className="search-input"
            placeholder="Search equipment..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button className="search-btn">🔍</button>
        </div>
      </div>

      <div className="filter-container">
        <select
          className="filter-select"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          {categoryOptions.map(cat => (
            <option key={cat} value={cat}>{cat}</option>
          ))}
        </select>
      </div>

      {filteredProducts.length > 0 ? (
        <div className="products-grid">
          {filteredProducts.map(product => (
            <div key={product.id} className="product-card">
              <div className="product-image">
                {getImage(product) ? (
                  <img src={getImage(product)} alt={product.name} />
                ) : (
                  <div className="product-placeholder">
                    {product.name.charAt(0)}
                  </div>
                )}
              </div>
              <div className="product-info">
                <h3>{product.name}</h3>
                <p className="product-sku">SKU: {product.sku}</p>
                <p className="product-description">{product.description}</p>
                <div className="product-meta">
                  <span className="badge badge-category">{getCategory(product)}</span>
                </div>
                <Link to={`/products/${product.id}`} className="btn btn-outline">
                  View Details
                </Link>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="no-products">
          <h3>No products found</h3>
          <p>{searchTerm ? 'Try adjusting your search terms' : 'No products available at the moment'}</p>
        </div>
      )}
    </div>
  );
};

export default Products;
