import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { getProducts as apiGetProducts, getCategories } from '../services/api';
import { toast } from 'react-toastify';
import './Products.css';

  const getCategory = (p) => p.category || 'Equipment';
  const getImage = (p) => p.image_url || '';
const Products = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const initialSearch = params.get('search') || '';
  const [searchTerm, setSearchTerm] = useState(initialSearch);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [category, setCategory] = useState('All Equipment');
  const navigate = useNavigate();
  const [categoryOptions, setCategoryOptions] = useState(['All Equipment']);

  useEffect(() => {
    // Load products with published & in-stock filters and fetch categories
    loadProducts();
    loadCategories();
  }, []);

  useEffect(() => {
    // Filter products based on search term
    const filtered = products.filter(product => {
      const inSearch =
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.sku.toLowerCase().includes(searchTerm.toLowerCase());
      const cat = getCategory(product);
      const inCategory = category === 'All Equipment' || cat === category;
      return inSearch && inCategory;
    });
    setFilteredProducts(filtered);
  }, [products, searchTerm, category]);

  const loadProducts = async () => {
    try {
      const response = await apiGetProducts({ published_only: true });
      setProducts(response.data || []);
    } catch (error) {
      console.error('Error loading products:', error);
      toast.error('Failed to load products');
    } finally {
      setLoading(false);
    }
  };

  const loadCategories = async () => {
    try {
      const res = await getCategories();
      const serverCats = Array.isArray(res.data) ? res.data : [];
      setCategoryOptions(['All Equipment', ...serverCats]);
    } catch (e) {
      console.error('Failed to load categories', e);
    }
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  // React to URL query param changes (e.g., from header search)
  useEffect(() => {
    const p = new URLSearchParams(location.search);
    const s = p.get('search') || '';
    setSearchTerm(s);
  }, [location.search]);

  const categories = categoryOptions;

  if (loading) {
    return (
      <div className="products-container">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading products...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-800 via-slate-700 to-slate-900">
      {/* Hero Header */}
      <header className="relative bg-gradient-to-r from-green-700 to-green-800 text-white overflow-hidden">
        <div className="absolute inset-0 bg-black opacity-20"></div>
        <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-6">
              <img 
                src="/Website Logo.jpg" 
                alt="Company Logo" 
                className="w-20 h-20 rounded-full border-4 border-white shadow-lg"
              />
              <div>
                <h1 className="text-4xl font-bold mb-2">Get The Job Done. Rent it</h1>
                <p className="text-xl text-green-100 max-w-2xl">
                  From excavators to power tools, we provide high-quality rental equipment to keep your projects running smoothly and on schedule.
                </p>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <div className="relative">
                <input
                  type="text"
                  placeholder="What are you looking for?"
                  className="w-80 px-4 py-3 rounded-lg text-gray-900 bg-white border-2 border-green-300 focus:border-green-500 focus:outline-none"
                  value={searchTerm}
                  onChange={handleSearch}
                />
                <svg className="absolute right-3 top-3 w-6 h-6 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
              </div>
            </div>
          </div>
        </div>
        {/* Construction equipment background overlay */}
        <div className="absolute right-0 top-0 h-full w-1/3 bg-gradient-to-l from-transparent to-green-800 opacity-30"></div>
      </header>

      {/* Filters */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex items-center justify-between mb-8">
          <div className="flex items-center space-x-4">
            <label className="text-white font-medium">Filter by Category:</label>
            <select
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent bg-white text-gray-900"
            >
              {categoryOptions.map(cat => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
          </div>

          <div className="text-white">
            <span className="text-lg font-medium">{filteredProducts.length} Equipment Available</span>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 pb-12">
        {filteredProducts.length > 0 ? (
          <div className="products-grid">
            {filteredProducts.map(product => (
              <div key={product.id} className="product-card" style={{ cursor: 'pointer' }} onClick={() => navigate(`/products/${product.id}`)}>
                <div className="product-image">
                  {getImage(product) ? (
                    <img src={getImage(product)} alt={product.name} style={{ width: '100%', height: '100%', objectFit: 'cover' }} onError={(e) => { e.currentTarget.style.display='none'; }} />
                  ) : (
                    <div className="product-placeholder">
                      {product.name.charAt(0)}
                    </div>
                  )}
                </div>
                <div className="product-info">
                  <h3>{product.name}</h3>
                  <p className="product-sku">SKU: {product.sku}</p>
                  <p className="product-description">{product.description}</p>
                  <div className="product-meta">
                    <span className="badge badge-category">{getCategory(product)}</span>
                  </div>
                  <Link to={`/products/${product.id}`} className="btn btn-outline">
                    View Details
                  </Link>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="no-products text-center py-12">
            <h3 className="text-white text-xl mb-4">No products found</h3>
            <p className="text-gray-300">{searchTerm ? 'Try adjusting your search terms' : 'No products available at the moment'}</p>
            {searchTerm && (
              <button
                onClick={() => setSearchTerm('')}
                className="mt-4 bg-green-600 hover:bg-green-700 text-white px-6 py-2 rounded-lg"
              >
                Clear Search
              </button>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Products;
