import React, { useEffect, useMemo, useState } from 'react';
import { useParams, Link, useNavigate, useLocation } from 'react-router-dom';
import { toast } from 'react-toastify';
import { getProduct, getAvailableItems, createOrder } from '../services/api';

const ProductDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const location = useLocation();

  const [loading, setLoading] = useState(true);
  const [product, setProduct] = useState(null);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [checking, setChecking] = useState(false);
  const [availableItems, setAvailableItems] = useState([]);
  const [selectedItemId, setSelectedItemId] = useState('');
  const [booking, setBooking] = useState(false);

  useEffect(() => {
    const load = async () => {
      try {
        const res = await getProduct(id);
        setProduct(res.data);
      } catch (e) {
        console.error(e);
        toast.error('Failed to load product');
      } finally {
        setLoading(false);
      }
    };
    load();
  }, [id]);

  // Prefill dates from query params and auto-check when both are present
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const s = params.get('start') || '';
    const e = params.get('end') || '';
    let changed = false;
    if (s && s !== startDate) { setStartDate(s); changed = true; }
    if (e && e !== endDate) { setEndDate(e); changed = true; }
    if (changed && s && e) {
      // defer to ensure state updated before check
      setTimeout(() => { onCheck(); }, 0);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [location.search]);

  const days = useMemo(() => {
    if (!startDate || !endDate) return 0;
    const s = new Date(startDate);
    const e = new Date(endDate);
    const diff = (e - s) / (1000 * 60 * 60 * 24);
    return diff > 0 ? diff : 0;
  }, [startDate, endDate]);

  const estimate = useMemo(() => {
    if (!product || !days) return 0;
    return Math.round((product.daily_rate || 0) * days * 100) / 100;
  }, [product, days]);

  const onCheck = async () => {
    if (!startDate || !endDate) {
      toast.info('Please select a start and end date');
      return;
    }
    if (days <= 0) {
      toast.warn('End date must be after start date');
      return;
    }
    try {
      setChecking(true);
      const res = await getAvailableItems(id, startDate, endDate);
      const items = Array.isArray(res.data) ? res.data : (res.data?.items || []);
      setAvailableItems(items);
      if (items.length === 0) {
        toast.warn('No units available for the selected period');
      } else {
        setSelectedItemId(items[0]?.id || '');
        toast.success('Availability confirmed');
      }
    } catch (e) {
      console.error(e);
      toast.error('Failed to check availability');
    } finally {
      setChecking(false);
    }
  };

  const onBook = async () => {
    if (!selectedItemId) {
      toast.info('Please select an available unit');
      return;
    }
    try {
      setBooking(true);
      const payload = {
        reservations: [
          { inventory_item_id: selectedItemId, start_date: startDate, end_date: endDate }
        ]
      };
      const res = await createOrder(payload);
      toast.success('Booking created');
      navigate('/orders');
    } catch (e) {
      console.error(e);
      const msg = e?.response?.data?.detail || 'Failed to create booking';
      toast.error(msg);
    } finally {
      setBooking(false);
    }
  };

  if (loading) {
    return (
      <div className="container" style={{ padding: '2rem 0', textAlign: 'center' }}>
        <p>Loading product...</p>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="container" style={{ padding: '2rem 0', textAlign: 'center' }}>
        <p>Product not found.</p>
        <Link to="/products" className="btn btn-primary">← Back to Products</Link>
      </div>
    );
  }

  return (
    <div className="container" style={{ padding: '2rem 0' }}>
      <div style={{ maxWidth: 1100, margin: '0 auto', display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '2rem' }}>
        <div>
          <div style={{ height: 320, background: 'linear-gradient(135deg,#667eea,#764ba2)', borderRadius: 12 }} />
          <h1 style={{ marginTop: '1rem', color: '#2c3e50' }}>{product.name}</h1>
          <p style={{ color: '#6c757d' }}>{product.description}</p>
          <div style={{ marginTop: '0.5rem', color: '#2c3e50', fontWeight: 600 }}>${product.daily_rate}/day</div>
        </div>

        <div style={{ background: '#fff', border: '1px solid #e9ecef', borderRadius: 12, padding: '1.25rem' }}>
          <h2 style={{ marginTop: 0, color: '#2c3e50' }}>Book this equipment</h2>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '0.75rem' }}>
            <div>
              <label style={{ display: 'block', fontSize: 14, color: '#2c3e50', marginBottom: 4 }}>Start date</label>
              <input type="date" value={startDate} onChange={(e)=>setStartDate(e.target.value)} style={{ width: '100%', padding: '0.6rem', borderRadius: 8, border: '1px solid #e1e5ea' }} />
            </div>
            <div>
              <label style={{ display: 'block', fontSize: 14, color: '#2c3e50', marginBottom: 4 }}>End date</label>
              <input type="date" value={endDate} onChange={(e)=>setEndDate(e.target.value)} style={{ width: '100%', padding: '0.6rem', borderRadius: 8, border: '1px solid #e1e5ea' }} />
            </div>
          </div>

          <div style={{ display: 'flex', gap: '0.5rem', marginTop: '0.75rem' }}>
            <button onClick={onCheck} disabled={checking || !startDate || !endDate} className="btn btn-primary" style={{ background: '#1b7a23' }}>
              {checking ? 'Checking…' : 'Check Availability'}
            </button>
            <Link to="/products" className="btn btn-outline">Back to Catalog</Link>
          </div>

          {availableItems.length > 0 && (
            <div style={{ marginTop: '1rem' }}>
              <label style={{ display: 'block', fontSize: 14, color: '#2c3e50', marginBottom: 4 }}>Select unit</label>
              <select value={selectedItemId} onChange={(e)=>setSelectedItemId(e.target.value)} style={{ width: '100%', padding: '0.6rem', borderRadius: 8, border: '1px solid #e1e5ea' }}>
                {availableItems.map((it) => (
                  <option key={it.id} value={it.id}>#{it.id} {it.serial ? `• ${it.serial}` : ''}</option>
                ))}
              </select>

              <div style={{ marginTop: '0.75rem', padding: '0.75rem', background: '#f8fafc', borderRadius: 8, border: '1px solid #e9ecef' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                  <span>Days</span>
                  <strong>{days}</strong>
                </div>
                <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: 6 }}>
                  <span>Rate</span>
                  <strong>${product.daily_rate}/day</strong>
                </div>
                <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: 6 }}>
                  <span>Estimated total</span>
                  <strong>${estimate}</strong>
                </div>
              </div>

              <button onClick={onBook} disabled={booking} className="btn btn-primary" style={{ marginTop: '0.75rem', background: '#1b7a23' }}>
                {booking ? 'Booking…' : 'Book Now'}
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
