import React from 'react';
import { useParams, Link } from 'react-router-dom';

const ProductDetail = () => {
  const { id } = useParams();

  return (
    <div className="container" style={{ padding: '2rem 0', textAlign: 'center' }}>
      <h1>Product Details</h1>
      <p>Viewing product ID: {id}</p>
      <div style={{ margin: '2rem 0' }}>
        <p>This page will show detailed product information, availability calendar, and booking options.</p>
        <p><strong>Coming Soon:</strong> Product images, specifications, pricing calculator, and booking form.</p>
      </div>
      <Link to="/products" className="btn btn-primary">
        ‚Üê Back to Products
      </Link>
    </div>
  );
};

export default ProductDetail;
