import React, { useState, useEffect } from 'react';
import { api } from '../services/api';

const Inventory = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('All Categories');

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await api.getProducts();
      setProducts(response.data || []);
    } catch (error) {
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusBadge = (status) => {
    const statusStyles = {
      available: 'bg-green-100 text-green-800 border border-green-200',
      reserved: 'bg-orange-100 text-orange-800 border border-orange-200',
      maintenance: 'bg-red-100 text-red-800 border border-red-200',
      rented: 'bg-blue-100 text-blue-800 border border-blue-200'
    };

    return (
      <span className={`absolute top-3 right-3 px-2 py-1 rounded-full text-xs font-medium ${statusStyles[status.toLowerCase()] || statusStyles.available}`}>
        {status.charAt(0).toUpperCase() + status.slice(1)}
      </span>
    );
  };

  const getProductImage = (productName) => {
    const imageMap = {
      'projector': 'https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=400&h=300&fit=crop',
      'sound': 'https://images.unsplash.com/photo-1493225457124-a3eb161ffa5f?w=400&h=300&fit=crop',
      'camera': 'https://images.unsplash.com/photo-1502920917128-1aa500764cbd?w=400&h=300&fit=crop',
      'lighting': 'https://images.unsplash.com/photo-1518709268805-4e9042af2176?w=400&h=300&fit=crop',
      'microphone': 'https://images.unsplash.com/photo-1590602847861-f357a9332bbc?w=400&h=300&fit=crop',
      'tripod': 'https://images.unsplash.com/photo-1606983340126-99ab4feaa64a?w=400&h=300&fit=crop',
      'screen': 'https://images.unsplash.com/photo-1560472354-b33ff0c44a43?w=400&h=300&fit=crop'
    };

    const key = Object.keys(imageMap).find(k => productName.toLowerCase().includes(k));
    return imageMap[key] || 'https://images.unsplash.com/photo-1558618666-fcd25c85cd64?w=400&h=300&fit=crop';
  };

  const mockProducts = [
    { id: 1, name: 'Projector Model X100', category: 'Projectors', daily_rate: 50, status: 'Available' },
    { id: 2, name: 'Sound System Pro 2000', category: 'Audio Equipment', daily_rate: 80, status: 'Reserved' },
    { id: 3, name: 'Camera Kit Deluxe', category: 'Cameras', daily_rate: 120, status: 'Available' },
    { id: 4, name: 'Lighting Kit Basic', category: 'Lighting', daily_rate: 40, status: 'Maintenance' },
    { id: 5, name: 'Microphone Set Wireless', category: 'Audio Equipment', daily_rate: 60, status: 'Available' },
    { id: 6, name: 'Tripod Heavy Duty', category: 'Accessories', daily_rate: 20, status: 'Available' },
    { id: 7, name: 'Projector Screen Large', category: 'Accessories', daily_rate: 30, status: 'Available' },
    { id: 8, name: 'Sound System Compact', category: 'Audio Equipment', daily_rate: 70, status: 'Rented' }
  ];

  const displayProducts = products.length > 0 ? products : mockProducts;
  const categories = ['All Categories', ...new Set(displayProducts.map(p => p.category))];

  const filteredProducts = displayProducts.filter(product => {
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = categoryFilter === 'All Categories' || product.category === categoryFilter;
    return matchesSearch && matchesCategory;
  });

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-gray-500">Loading inventory...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      {/* Header */}
      <div className="bg-gray-800 px-6 py-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-4">
            <div className="flex items-center gap-2">
              <div className="w-8 h-8 bg-green-500 rounded flex items-center justify-center">
                <span className="text-white font-bold text-sm">S</span>
              </div>
              <span className="text-white font-semibold">Smart Systems</span>
            </div>
            <nav className="flex items-center gap-6 ml-8">
              <a href="/dashboard" className="text-gray-300 hover:text-white">Dashboard</a>
              <a href="/orders" className="text-gray-300 hover:text-white">Orders</a>
              <a href="/products" className="text-gray-300 hover:text-white">Products</a>
              <a href="/inventory" className="text-white font-medium">Inventory</a>
              <a href="/customers" className="text-gray-300 hover:text-white">Customers</a>
              <a href="/reports" className="text-gray-300 hover:text-white">Reports</a>
              <a href="/settings" className="text-gray-300 hover:text-white">Settings</a>
            </nav>
          </div>
          <div className="flex items-center gap-4">
            <div className="w-6 h-6 text-gray-400">
              <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 17h5l-5 5v-5zM9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <span className="text-white text-sm">Admin</span>
            <span className="text-gray-400 text-sm">admin@smartsystems.com</span>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="p-6">
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-2xl font-bold">Inventory Management</h1>
          <button className="bg-green-500 text-white px-4 py-2 rounded-lg flex items-center gap-2 hover:bg-green-600">
            <span className="text-lg">+</span>
            Add Item
          </button>
        </div>

        {/* Search and Filter */}
        <div className="flex gap-4 mb-6">
          <div className="relative flex-1">
            <input
              type="text"
              placeholder="Search inventory..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-2 bg-gray-800 border border-gray-700 rounded-lg text-white placeholder-gray-400 focus:ring-2 focus:ring-green-500 focus:border-transparent"
            />
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <svg className="h-5 w-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
              </svg>
            </div>
          </div>
          <select
            value={categoryFilter}
            onChange={(e) => setCategoryFilter(e.target.value)}
            className="px-4 py-2 bg-gray-800 border border-gray-700 rounded-lg text-white focus:ring-2 focus:ring-green-500 focus:border-transparent"
          >
            {categories.map(category => (
              <option key={category} value={category}>{category}</option>
            ))}
          </select>
        </div>

        {/* Product Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {filteredProducts.map((product) => (
            <div key={product.id} className="bg-gray-800 rounded-lg overflow-hidden hover:bg-gray-750 transition-colors">
              <div className="relative">
                <img
                  src={getProductImage(product.name)}
                  alt={product.name}
                  className="w-full h-48 object-cover"
                />
                {getStatusBadge(product.status)}
              </div>
              <div className="p-4">
                <h3 className="text-lg font-semibold text-white mb-2">{product.name}</h3>
                <p className="text-gray-400 text-sm mb-3">{product.category}</p>
                <div className="flex items-center justify-between">
                  <span className="text-green-400 font-bold">${product.daily_rate}/day</span>
                  <button className="text-blue-400 hover:text-blue-300 text-sm">
                    View Details
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>

        {filteredProducts.length === 0 && (
          <div className="text-center py-12">
            <div className="text-gray-400 text-lg">No items found matching your criteria</div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Inventory;
