import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Layout = ({ children }) => {
  const location = useLocation();

  const navItems = [
    { path: '/', label: 'Dashboard' },
    { path: '/admin', label: 'Admin Panel' },
    { path: '/products', label: 'Products' },
    { path: '/inventory', label: 'Inventory' },
    { path: '/customers', label: 'Customers' },
    { path: '/orders', label: 'Orders' },
    { path: '/reports', label: 'Reports' }
  ];

  return (
    <div style={{ display: 'flex', minHeight: '100vh' }}>
      {/* Sidebar */}
      <nav style={{
        width: '250px',
        backgroundColor: '#2c3e50',
        color: 'white',
        padding: '20px 0'
      }}>
        <div style={{ padding: '0 20px', marginBottom: '30px' }}>
          <h2 style={{ margin: 0, color: '#ecf0f1' }}>SmartRentals</h2>
          <p style={{ margin: '5px 0 0 0', color: '#bdc3c7', fontSize: '14px' }}>Admin Dashboard</p>
        </div>

        <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
          {navItems.map(item => (
            <li key={item.path}>
              <Link
                to={item.path}
                style={{
                  display: 'block',
                  padding: '12px 20px',
                  color: location.pathname === item.path ? '#3498db' : '#ecf0f1',
                  textDecoration: 'none',
                  backgroundColor: location.pathname === item.path ? '#34495e' : 'transparent',
                  borderLeft: location.pathname === item.path ? '4px solid #3498db' : '4px solid transparent'
                }}
              >
                {item.label}
              </Link>
            </li>
          ))}
        </ul>
      </nav>

      {/* Main content */}
      <main style={{
        flex: 1,
        padding: '20px',
        backgroundColor: '#f8f9fa'
      }}>
        {children}
      </main>
    </div>
  );
};

export default Layout;
